function [output] = applyITD(inputSignal, itd_us, fs)inputSignal(:,2) = inputSignal;% round ITD to samples (ints)itd_samples = (itd_us/1e6)*fs;actualItd_samples = round(itd_samples);if actualItd_samples == 0    left = inputSignal(:,1);    right = inputSignal(:,2);elseif actualItd_samples > 0    delay = zeros(actualItd_samples,1);    left = [delay; inputSignal(:,1)];    right = [inputSignal(:,2); delay];elseif actualItd_samples < 0    delay = zeros(abs(actualItd_samples),1);    left = [inputSignal(:,1); delay];    right = [delay; inputSignal(:,2)];end% return valuesoutput = [left, right];